import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';
import x from './img/x.png'
import o from './img/o.png'

const socket = io.connect("http://localhost:3001/");


function App() {

  class Game {
    constructor() {
      this.state = {
        board: new Array(9).fill(null),
        assignment: '',
        turn:  'X',


      }
    }
  }

  const [boardShown, setBoardShown] = useState(false);
  const [matchmakingShown, setMatchmakingShown] = useState(false);
  const [buttonShown, setButtonShown] = useState(true);

  const game = new Game();

  const showMatchmaking = () => {
    setMatchmakingShown(true); 
   };

  const hideButton = () => {
    setButtonShown(false)
  }

  const select = (block) => {


    /*
    if (!selected.includes(block.target.getAttribute('id')) && my_turn && !taken.includes(block.target.getAttribute('id')) && block.target.getAttribute('id') != null) {
      var blockID = block.target.getAttribute('id');
      socket.emit("select_block", blockID);
      addBlock(blockID)
      setTurn(false);
      let win_test = [...selected, blockID];

      if ((win_test.includes("TL") && win_test.includes("TC") && win_test.includes("TR")) || 
      (win_test.includes("CL") && win_test.includes("CC") && win_test.includes("CR")) || 
      (win_test.includes("BL") && win_test.includes("BC") && win_test.includes("BR")) || 
      (win_test.includes("BL") && win_test.includes("CL") && win_test.includes("TL")) || 
      (win_test.includes("BC") && win_test.includes("CC") && win_test.includes("TC")) || 
      (win_test.includes("BR") && win_test.includes("CR") && win_test.includes("TR")) || 
      (win_test.includes("BL") && win_test.includes("CC") && win_test.includes("TR")) || 
      (win_test.includes("BR") && win_test.includes("CC") && win_test.includes("TL"))) {
        setTimeout(function() {
          alert('You Win!')
          setSelected([]);
          setTaken([]);
        },500);
      }
    }
    */
  }


  const startMatchmaking = () => {
    socket.emit("start_matchmaking");
    showMatchmaking();
    hideButton();
  };

  useEffect(() => {

    socket.on("receive_block", (block) => {
      socket.emit("change_turn");

      let win_test = [];


      if ((win_test.includes("TL") && win_test.includes("TC") && win_test.includes("TR")) || 
      (win_test.includes("CL") && win_test.includes("CC") && win_test.includes("CR")) || 
      (win_test.includes("BL") && win_test.includes("BC") && win_test.includes("BR")) || 
      (win_test.includes("BL") && win_test.includes("CL") && win_test.includes("TL")) || 
      (win_test.includes("BC") && win_test.includes("CC") && win_test.includes("TC")) || 
      (win_test.includes("BR") && win_test.includes("CR") && win_test.includes("TR")) || 
      (win_test.includes("BL") && win_test.includes("CC") && win_test.includes("TR")) || 
      (win_test.includes("BR") && win_test.includes("CC") && win_test.includes("TL"))) {
        setTimeout(function() {
          alert('You Lose!')
        },500);
      }
    });

    socket.on("connectGame", (gameID) => {
      setBoardShown(true);
      setMatchmakingShown(false);

      socket.emit("join_room", gameID);
    });

    socket.on("player_disconnected", () => {
      setBoardShown(false);
      setMatchmakingShown(false);
      setButtonShown(true);

      socket.emit("leave_room");
    });

    socket.on("announce", (statement) => {
      console.log(statement);

    });

  }, [socket]);

  console.log(game.board);

  return (
    <div className="App">
      <div className="matchMaking">
        <button onClick={startMatchmaking}  className={`matchmaking_button ${buttonShown ? "" : " hidden"}`}>Start Matchmaking</button>
      </div> 
      <div className={`matchmaking ${matchmakingShown ? "" : " hidden"}`}>
        <p>Waiting for opponent...</p>
      </div>
      <div className={`board ${boardShown ? "" : " hidden"}`}>

        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[0] === null ? "available" : ""}`} id="0">
          {game.board[0] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[0] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[1] === null ? "available" : ""}`} id="1">

          {game.board[1] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[1] === 'O' ? <img className="o" src={o} alt=""/> : ""}

        </button>

        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[2] === null ? "available" : ""}`} id="2">
          {game.board[2] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[2] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>
        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[3] === null ? "available" : ""}`} id="3">
          {game.board[3] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[3] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[4] === null ? "available" : ""}`} id="4">
          {game.board[4] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[4] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[5] === null ? "available" : ""}`} id="5">
          {game.board[5] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[5] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[6] === null ? "available" : ""}`} id="6">
          {game.board[6] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[6] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[7] === null ? "available" : ""}`} id="7">
          {game.board[7] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[7] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${game.turn === game.assignment ? "my_turn" : ""} ${game.board[8] === null ? "available" : ""}`} id="8">
          {game.board[8] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {game.board[8] === 'O' ? <img className="o" src={o} alt=""/> : ""}

        </button>
      </div> 
    </div>
  );
}

export default App;
