import './App.css';
import io from 'socket.io-client';
import { useEffect } from 'react';


const socket = io.connect("http://10.2.105.97:3001/");



function App() {

  const select = (block) => {
    socket.emit("select_block", block.target.getAttribute('id'));
  }

  useEffect(() => {
    socket.on("receive_block", (block) => {
      console.log(block);
    });
  }, [socket]);

  return (
    <div className="App">
      <div className="board">

        <button onClick={select} className="Spot" id="TL"></button>
        <button onClick={select} className="Spot" id="TC"></button>
        <button onClick={select} className="Spot" id="TR"></button>
        <button onClick={select} className="Spot" id="CL"></button>
        <button onClick={select} className="Spot" id="CC"></button>
        <button onClick={select} className="Spot" id="CR"></button>
        <button onClick={select} className="Spot" id="BL"></button>
        <button onClick={select} className="Spot" id="BC"></button>
        <button onClick={select} className="Spot" id="BR"></button>
      </div> 
    </div>
  );
}

export default App;
