import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';
import x from './img/x.png'
import o from './img/o.png'

const socket = io.connect("http://localhost:3001/");


class board extends Component.React {

    constructor(props) {
        super(props);
        this.state = {
            game: new Array(9).fill(null),
            assignment: '',
            turn: 'X',


        }
    }

    select() {

    }

    render() {
        return (

        <div className={`board`}>

        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[0] === null ? "available" : ""}`} id="0">
          {this.board[0] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[0] === 'O' ? <img className="o" src={o} alt=""/> : ""}
          <img src={x} alt=""/>
        </button>

        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[1] === null ? "available" : ""}`} id="1">

          {this.board[1] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[1] === 'O' ? <img className="o" src={o} alt=""/> : ""}

        </button>

        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[2] === null ? "available" : ""}`} id="2">
          {this.board[2] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[2] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>
        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[3] === null ? "available" : ""}`} id="3">
          {this.board[3] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[3] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[4] === null ? "available" : ""}`} id="4">
          {this.board[4] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[4] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[5] === null ? "available" : ""}`} id="5">
          {this.board[5] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[5] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[6] === null ? "available" : ""}`} id="6">
          {this.board[6] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[6] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[7] === null ? "available" : ""}`} id="7">
          {this.board[7] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[7] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${this.turn === this.assignment ? "my_turn" : ""} ${this.board[8] === null ? "available" : ""}`} id="8">
          {this.board[8] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.board[8] === 'O' ? <img className="o" src={o} alt=""/> : ""}

        </button>
      </div> 
      );
    }

}

export default board
