import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';
import x from './img/x.png'
import o from './img/o.png'

const socket = io.connect("http://localhost:3001/");


function App() {

  const [boardShown, setBoardShown] = useState(false);
  const [matchmakingShown, setMatchmakingShown] = useState(false);
  const [buttonShown, setButtonShown] = useState(true);

  const [my_turn, setTurn] = useState(true);

  const [selected, setSelected] = useState([]);
  const [taken, setTaken] = useState([]);

  const showMatchmaking = () => {
    setMatchmakingShown(true); 
   };

  const hideButton = () => {
    setButtonShown(false)
  }

  const select = (block) => {
    if (!selected.includes(block.target.getAttribute('id')) && my_turn && !taken.includes(block.target.getAttribute('id')) && block.target.getAttribute('id') != null) {
      var blockID = block.target.getAttribute('id');
      socket.emit("select_block", blockID);
      addBlock(blockID)
      setTurn(false);
    }
  }

  const addBlock = (block) => {
    setSelected([...selected, block]);
  }

  const addTakenBlock = (block) => {
    setTaken([...taken, block]);
  }

  const startMatchmaking = () => {
    socket.emit("start_matchmaking");
    showMatchmaking();
    hideButton();
  };

  useEffect(() => {

    socket.on("receive_block", (block) => {
      addTakenBlock(block);
      socket.emit("change_turn");
      setTurn(true);
    });

    socket.on("connectGame", (gameID) => {
      setBoardShown(true);
      setMatchmakingShown(false);

      socket.emit("join_room", gameID);

      setSelected([]);
    });

    socket.on("player_disconnected", () => {
      setBoardShown(false);
      setMatchmakingShown(false);
      setButtonShown(true);

      socket.emit("leave_room");

      setSelected([]);

    });

  }, [socket, taken]);

  return (
    <div className="App">
      <div className="matchMaking">
        <button onClick={startMatchmaking}  className={`matchmaking_button ${buttonShown ? "" : " hidden"}`}>Start Matchmaking</button>
      </div> 
      <div className={`matchmaking ${matchmakingShown ? "" : " hidden"}`}>
        <p>Waiting for opponent...</p>
      </div>
      <div className={`board ${boardShown ? "" : " hidden"}`}>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("TL") && !taken.includes("TL")) ? "available" : ""}`} id="TL">
          {selected.includes("TL") ? <img src={x} alt=""/> : ""}
          {taken.includes("TL") ? <img src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("TC") && !taken.includes("TC")) ? "available" : ""}`} id="TC">

          {selected.includes("TC") ? <img src={x} alt=""/> : ""}
          {taken.includes("TC") ? <img src={o} alt=""/> : ""}

        </button>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("TR") && !taken.includes("TR")) ? "available" : ""}`} id="TR">
          {selected.includes("TR") ? <img src={x} alt=""/> : ""}
          {taken.includes("TR") ? <img src={o} alt=""/> : ""}
        </button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("CL") && !taken.includes("CL")) ? "available" : ""}`} id="CL">
          {selected.includes("CL") ? <img src={x} alt=""/> : ""}
          {taken.includes("CL") ? <img src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("CC") && !taken.includes("CC")) ? "available" : ""}`} id="CC">
          {selected.includes("CC") ? <img src={x} alt=""/> : ""}
          {taken.includes("CC") ? <img src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("CR") && !taken.includes("CR")) ? "available" : ""}`} id="CR">
          {selected.includes("CR") ? <img src={x} alt=""/> : ""}
          {taken.includes("CR") ? <img src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("BL") && !taken.includes("BL")) ? "available" : ""}`} id="BL">
          {selected.includes("BL") ? <img src={x} alt=""/> : ""}
          {taken.includes("BL") ? <img src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("BC") && !taken.includes("BC")) ? "available" : ""}`} id="BC">
          {selected.includes("BC") ? <img src={x} alt=""/> : ""}
          {taken.includes("BC") ? <img src={o} alt=""/> : ""}
        </button>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${(!selected.includes("BR") && !taken.includes("BR")) ? "available" : ""}`} id="BR">
          {selected.includes("BR") ? <img src={x} alt=""/> : ""}
          {taken.includes("BR") ? <img src={o} alt=""/> : ""}

        </button>
      </div> 
    </div>
  );
}

export default App;
