import './App.css';
import io from 'socket.io-client';
import { Component, useEffect, useState } from 'react';
import x from "./img/x.png"
import o from "./img/o.png"

let socket = io("http://localhost:3001/");


function App() {

class Board extends Component {

    constructor(props) {
        super(props);
        this.state = {
            board: new Array(9).fill(null),
            assignment: '',
            turn: 'X',
        }

        this.socket = this.props.sent_socket;
    }

    componentDidMount() {

      this.socket = io.connect(this.socket);
      console.log(this.socket);


      this.socket.on(`announce`, text => {
        console.log(text);
      });

      this.socket.on("connectGame", gameID => {
        //console.log("howdy");
        this.socket.emit("join_room", gameID);
      });

      this.socket.on("receive_block", (block) => {
        this.socket.emit("change_turn");

      });

      this.socket.on("player_disconnected", () => {
        this.socket.emit("leave_room");
      });
    }

    socket() {

    }

    render() {
        return (

        <div className={`board`}>

        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[0] === null ? "available" : ""}`} id="0">
          {this.state.board[0] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[0] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[1] === null ? "available" : ""}`} id="1">

          {this.state.board[1] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[1] === 'O' ? <img className="o" src={o} alt=""/> : ""}

        </button>

        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[2] === null ? "available" : ""}`} id="2">
          {this.state.board[2] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[2] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>
        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[3] === null ? "available" : ""}`} id="3">
          {this.state.board[3] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[3] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[4] === null ? "available" : ""}`} id="4">
          {this.state.board[4] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[4] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[5] === null ? "available" : ""}`} id="5">
          {this.state.board[5] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[5] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[6] === null ? "available" : ""}`} id="6">
          {this.state.board[6] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[6] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[7] === null ? "available" : ""}`} id="7">
          {this.state.board[7] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[7] === 'O' ? <img className="o" src={o} alt=""/> : ""}
        </button>

        <button onClick={this.select} className={`Spot ${this.state.turn === this.state.assignment ? "my_turn" : ""} ${this.state.board[8] === null ? "available" : ""}`} id="8">
          {this.state.board[8] === 'X' ? <img className="x" src={x} alt=""/> : ""}
          {this.state.board[8] === 'O' ? <img className="o" src={o} alt=""/> : ""}

        </button>
      </div> 
      );
    }

}

  const [boardShown, setBoardShown] = useState(false);
  const [matchmakingShown, setMatchmakingShown] = useState(false);
  const [buttonShown, setButtonShown] = useState(true);

  const showMatchmaking = () => {
    setMatchmakingShown(true); 
   };

  const hideButton = () => {
    setButtonShown(false)
  }


  const startMatchmaking = () => {
    socket.emit("start_matchmaking");
    showMatchmaking();
    hideButton();
  };

  useEffect(() => {

    socket.on("connectGame", (gameID) => {
      setBoardShown(true);
      setMatchmakingShown(false);

      socket.emit("join_room", gameID);
    });

    socket.on("player_disconnected", () => {
      setBoardShown(false);
      setMatchmakingShown(false);
      setButtonShown(true);

      socket.emit("leave_room");
    });

  }, [socket]);

  return (
    <div className="App">
      <div className="matchMaking">
        <button onClick={startMatchmaking}  className={`matchmaking_button ${buttonShown ? "" : " hidden"}`}>Start Matchmaking</button>
      </div> 
      <div className={`matchmaking ${matchmakingShown ? "" : " hidden"}`}>
        <p>Waiting for opponent...</p>
      </div>

      {boardShown ? <Board sent_socket = {socket }/> : "" }
    </div>
  );
}

export default App;
