import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';


const socket = io.connect("http://localhost:3001/");



function App() {

  const [room, setRoom] = useState("");

  const [boardShown, setBoardShown] = useState(false);
  const [matchmakingShown, setMatchmakingShown] = useState(false);
  const [buttonShown, setButtonShown] = useState(true);

  const [my_turn, setTurn] = useState(true);

  const [selected, setSelected] = useState([]);

  const showMatchmaking = () => {
    setMatchmakingShown(true); 
   };

  const hideButton = () => {
    setButtonShown(false)
  }



  const select = (block) => {
    var blockID = block.target.getAttribute('id');
    socket.emit("select_block", blockID);
    setSelected(selected.concat(block.target.getAttribute('id')));
  }

  const startMatchmaking = () => {
    socket.emit("start_matchmaking");
    showMatchmaking();
    hideButton();
  };

  useEffect(() => {
    socket.on("receive_block", (block) => {
      setSelected(selected.concat(block));
      socket.emit("change_turn");
    });

    socket.on("connectGame", (gameID) => {
      setBoardShown(true);
      setMatchmakingShown(false);

      socket.emit("join_room", gameID);

      setRoom(gameID);

    });



    socket.on("player_disconnected", () => {
      setBoardShown(false);
      setMatchmakingShown(false);
      setButtonShown(true);

      socket.emit("leave_room");

      setRoom("");

    });

  }, [socket]);

  return (
    <div className="App">
      <div className="matchMaking">
        <button onClick={startMatchmaking}  className={`matchmaking_button ${buttonShown ? "" : " hidden"}`}>Start Matchmaking</button>
      </div> 
      <div className={`matchmaking ${matchmakingShown ? "" : " hidden"}`}>
        <p>Waiting for opponent...</p>
      </div>
      <div className={`board ${boardShown ? "" : " hidden"}`}>

        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("TL") ? "available" : ""}`} id="TL"></button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("TC") ? "available" : ""}`} id="TC"></button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("TR") ? "available" : ""}`} id="TR"></button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("CL") ? "available" : ""}`} id="CL"></button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("CC") ? "available" : ""}`} id="CC"></button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("CR") ? "available" : ""}`} id="CR"></button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("BL") ? "available" : ""}`} id="BL"></button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("BC") ? "available" : ""}`} id="BC"></button>
        <button onClick={select} className={`Spot ${my_turn ? "my_turn" : ""} ${!selected.includes("BR") ? "available" : ""}`} id="BR"></button>
      </div> 
    </div>
  );
}

export default App;
