import "./App.css";
import io from "socket.io-client";
import { useEffect, useState } from "react";
import x from "./img/x.png";
import o from "./img/o.png";

let socket = io("http://localhost:3001/");

function App() {

  const [boardShown, setBoardShown] = useState(false);
  const [matchmakingShown, setMatchmakingShown] = useState(false);
  const [buttonShown, setButtonShown] = useState(true);
  var room = "";

  const [board, setBoard] = useState(new Array(9).fill(null));
  const [assignment, setAssignment] = useState("")
  const [turn, setTurn] = useState("X");

  const showMatchmaking = () => {
    setMatchmakingShown(true);
  };

  const hideButton = () => {
    setButtonShown(false);
  };

  const startMatchmaking = () => {
    socket.emit("start_matchmaking");
    showMatchmaking();
    hideButton();
  };

  useEffect(() => {
    socket.on("connectGame", (gameID) => {
      setBoardShown(true);
      setMatchmakingShown(false);
      room = gameID;


      socket.emit("join_room", gameID);
    });

    socket.on("player_disconnected", () => {
      setBoardShown(false);
      setMatchmakingShown(false);
      setButtonShown(true);

      socket.emit("leave_room");
    });

    socket.on("set_assignment", (assign) => {
      setAssignment(assign);
      console.log(assign, assignment, turn);
    });

  }, [socket, assignment]);

  const select = (e) => {
    var block = e.target.id;
    console.log(block);
    socket.emit("select_block", { block, room } );
  }

  return (
    <div className="App">
      <div className="matchMaking">
        <button
          onClick={startMatchmaking}
          className={`matchmaking_button ${buttonShown ? "" : " hidden"}`}
        >
          Start Matchmaking
        </button>
      </div>
      <div className={`matchmaking ${matchmakingShown ? "" : " hidden"}`}>
        <p>Waiting for opponent...</p>
      </div>

      <div className={`board ${boardShown ? "" : " hidden"}`}>
          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : "" } ${ board[0] === null ? "available" : "" }`}
            id="0"
          >
            {board[0] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[0] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>

          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : ""} ${board[1] === null ? "available" : ""}`}
            id="1"
          >
            {board[1] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[1] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>

          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : ""} ${board[2] === null ? "available" : ""}`}
            id="2"
          >
            {board[2] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[2] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>
          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : ""} ${board[3] === null ? "available" : ""}`}
            id="3"
          >
            {board[3] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[3] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>

          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : ""} ${board[4] === null ? "available" : ""}`}
            id="4"
          >
            {board[4] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[4] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>

          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : ""} ${board[5] === null ? "available" : ""}`}
            id="5"
          >
            {board[5] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[5] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>

          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : ""} ${board[6] === null ? "available" : ""}`}
            id="6"
          >
            {board[6] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[6] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>

          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : ""} ${board[7] === null ? "available" : ""}`}
            id="7"
          >
            {board[7] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[7] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>

          <button
            onClick={select}
            className={`Spot ${ turn === assignment ? "my_turn" : ""} ${board[8] === null ? "available" : ""}`}
            id="8"
          >
            {board[8] === "X" ? <img className="x" src={x} alt="" /> : ""}
            {board[8] === "O" ? <img className="o" src={o} alt="" /> : ""}
          </button>
        </div>
    </div>
  );
}

export default App;
