import './App.css';
import io from 'socket.io-client';
import { useEffect, useState } from 'react';


const socket = io.connect("http://localhost:3001/");



function App() {

  const [room, setRoom] = useState("");

  const [boardShown, setBoardShown] = useState(false);
  const [matchmakingShown, setMatchmakingShown] = useState(false);
  const [buttonShown, setButtonShown] = useState(true);

  const showBoard = () => {
    setBoardShown(true);
    hideMatchmaking();
    hideButton();
   };

  const hideBoard = () => {
    setBoardShown(false)
  }

  const showMatchmaking = () => {
    setMatchmakingShown(true); 
   };

  const hideMatchmaking = () => {
    setBoardShown(false)
  }

  const showButton = () => {
    setButtonShown(true); 
   };

  const hideButton = () => {
    setButtonShown(false)
  }



  const select = (block) => {
    var blockID = block.target.getAttribute('id')
    socket.emit("select_block", { blockID, room });
  }

  const startMatchmaking = () => {
    socket.emit("start_matchmaking");
    showMatchmaking();
    hideButton();
  };

  useEffect(() => {
    socket.on("receive_block", (block) => {
      console.log(block);
    });

    socket.on("connectGame", (gameID) => {
      setBoardShown(true);
      setMatchmakingShown(false);

      socket.emit("join_room", gameID);

      setRoom(gameID);

    });

    socket.on("connectGame", (gameID) => {
      setBoardShown(false);
      setMatchmakingShown(false);
      setBoardShown(true);

      socket.emit("leave_room", gameID);

      setRoom("");

    });

  }, [socket]);

  return (
    <div className="App">
      <div className="matchMaking">
        <button onClick={startMatchmaking}  className={`matchmaking_button ${buttonShown ? "" : " hidden"}`}>Start Matchmaking</button>
      </div> 
      <div className={`matchmaking ${matchmakingShown ? "" : " hidden"}`}>
        <p>Waiting for opponent...</p>
      </div>
      <div className={`board ${boardShown ? "" : " hidden"}`}>

        <button onClick={select} className="Spot" id="TL"></button>
        <button onClick={select} className="Spot" id="TC"></button>
        <button onClick={select} className="Spot" id="TR"></button>
        <button onClick={select} className="Spot" id="CL"></button>
        <button onClick={select} className="Spot" id="CC"></button>
        <button onClick={select} className="Spot" id="CR"></button>
        <button onClick={select} className="Spot" id="BL"></button>
        <button onClick={select} className="Spot" id="BC"></button>
        <button onClick={select} className="Spot" id="BR"></button>
      </div> 
    </div>
  );
}

export default App;
